import React from 'react';
import './App.css';

class App extends React.Component {

  state = {
    numLeft: "",
    numLeftActive: true,
    operator: [],
    numRight: "",
    numRightActive: false,
    result: 0,
  }
  calculation = (number) => {
   console.log("My current number is " + number)  

    if ( this.state.numLeftActive ) {
      this.setState({
        numLeft: number
      });
    } else if ( this.state.numRightActive ) {
      this.setState({
        numRightActive: number
      })

    }

  }
  operator = (op) => {
    console.log("My current operator is " + op);
    this.setState({
      numLeftActive: false,
      operator: op,
      numRightActive: true,
    })
  }

  setResult = () => {
    if( this.state.numLeft && this.state.numRight ) {
      let finalResult = 0;
      if( this.state.op === "+" ) {
        finalResult = this.state.numLeft + this.state.numRight;
      } else if( this.state.operator === "-" ) {
        finalResult = this.state.numLeft - this.state.numRight;
      } else if( this.state.operator === "x" ) {
        finalResult = this.state.numLeft * this.state.numRight;
      } else {
        finalResult = this.state.numLeft / this.state.numRight;
      }
      this.setState({
        result: finalResult
      });
    }
  }

  render() {
  console.log("My current state is ");
  console.log(this.state);

    return (
      <div className="box">
        <div>
          <h1 className="value">{this.state.currentValue}</h1>
        </div>
        <div>
          <button id ="clear" >clear</button>
          <button id ="/" onClick={ () => this.operator("/")}>÷</button>
          <button id ="*" onClick={ () => this.operator("*")}>*</button>
        </div>
        <div>
          <button id ="7" onClick={ () => this.calculation(7) }>7</button>
          <button id ="8" onClick={ () => this.calculation(8) }>8</button>
          <button id ="9" onClick={ () => this.calculation(9) }>9</button>
          <button id ="-" onClick={ () => this.operator("-")}>-</button>
        </div>
        <div>
          <button id ="4" onClick={ () => this.calculation(4) }>4</button>
          <button id ="5" onClick={ () => this.calculation(5) }>5</button>
          <button id ="6" onClick={ () => this.calculation(6) }>6</button>
          <button id ="+" onClick={ () => this.operator("+")}>+</button>
        </div>
        <div>
          <button id ="1" onClick={ () => this.calculation(1) }>1</button>
          <button id ="2" onClick={ () => this.calculation(2) }>2</button>
          <button id ="3" onClick={ () => this.calculation(3) }>3</button>
          <button id ="=" onClick={this.setResult("=")}>=</button>
        </div>
      </div>
    )
  }
}
export default App;
